mapper.select:
SELECT c.* FROM ( SELECT b.rn as rn , b.inv_no as invNo , b.inv_ttl as invTtl , b.inv_dt as invDt , b.po_no as poNo , b.mfgd_nm as mfgdNm , b.qty as qty , b.po_amt as poAmt , f.dept_nm as uppDeptNm , b.upp_dept_cd as uppDeptCd , b.dept_nm as deptNm , b.dept_cd as deptCd , b.inv_reqr as invReqr , b.user_nm as userNm , b.user_id as userId , b.asetQty , b.qty - b.asetQty as noAsetQty FROM ( SELECT a.rn , a.inv_no , a.inv_ttl , date_format(a.inv_dt, '%Y-%m-%d')as inv_dt , a.po_no , a.mfgd_nm , a.qty , a.po_amt , a.upp_dept_cd , a.dept_cd , a.dept_nm , a.inv_reqr , a.user_nm , a.user_id , IFNULL((SELECT qty FROM tb_po_aset B WHERE 1 = 1 AND B.PO_NO = A.PO_NO), 0) as asetQty FROM ( SELECT ROW_NUMBER() OVER(ORDER BY CAST(b.po_no AS int) DESC) as rn , a.inv_no , a.inv_ttl , b.po_no , b.mfgd_nm , b.qty , a.inv_dt , b.po_amt , e.upp_dept_cd , e.dept_cd , e.dept_nm , a.inv_reqr , d.user_nm , d.user_id FROM TB_INV_MAS A, TB_PO B, TB_USER D, TB_DEPT E WHERE 1 = 1 AND A.inv_no = B.inv_no AND D.use_yn = 'Y' AND E.use_yn = 'Y' AND A.regr = D.user_id AND D.dept_cd = E.dept_cd
AND a.inv_no LIKE CONCAT('%', #{params.invNo}, '%')
AND a.inv_ttl LIKE CONCAT('%', #{params.invTtl}, '%')
AND B.po_no LIKE CONCAT('%', #{params.poNo}, '%')
AND B.mfgd_nm LIKE CONCAT('%', #{params.mfgdNm}, '%')
AND E.dept_cd LIKE CONCAT('%', #{params.deptCd}, '%')
AND D.user_id LIKE CONCAT('%', #{params.userId}, '%')
AND A.inv_dt BETWEEN #{params.fromDate} AND #{params.toDate}
) AS a ) AS b LEFT OUTER JOIN TB_DEPT F ON f.use_yn = 'Y' AND f.dept_cd = b.upp_dept_cd WHERE rn BETWEEN #{start} AND #{end}
AND B.asetQty = 0
) as c
ORDER BY ${params.sortField} ${params.sortOrder}
ORDER BY c.poNo DESC

mapper.insert:
		INSERT INTO TB_INV_MAS(INV_NO, INV_TTL, INV_QTY, INV_AMT, ACTG_YEAR, INV_DT, INV_REQR, REG_DT, REGR)
                VALUES
                ( #{invNo}
                , #{invTtl}
                , CAST(#{invQty} as int)
                , CAST(#{invAmt} as int)
                , #{actgYear}
                , #{invDt}
                , #{regr}
                , now()
                , #{regr} )
                ON DUPLICATE KEY
                UPDATE INV_TTL = #{invTtl}
                     , INV_DT = #{invDt}
                     , INV_QTY = CAST(#{invQty} as int)
                     , INV_AMT = CAST(#{invAmt} as int)
                     , INV_REQR = #{regr}
                     , UPDR = #{updr}
                     , UP_DT = now()

		INSERT INTO TB_PO(PO_NO, INV_NO, MFGD_NM, QTY, PO_AMT, PO_CHRGR, VEND_NM, REQ_DT, DLV_DT, REGR)
                VALUES 
                ( #{poNo}
                , #{invNo}
                , #{mfgdNm}
                , CAST(#{qty} as int)
                , CAST(#{poAmt} as int)
                , #{regr} 
                , #{vendNm}
                , #{reqDt}
                , #{dlvDt}
                , #{regr} )
                ON DUPLICATE KEY
                UPDATE PO_CHRGR = #{regr}
                     , MFGD_NM = #{mfgdNm}
                     , QTY = CAST(#{qty} as int)
                     , PO_AMT = CAST(#{poAmt} as int)
                     , VEND_NM = #{vendNm}
                     , REQ_DT = #{reqDt}
                     , DLV_DT = #{dlvDt}
                     , UPDR = #{updr}
                     , UP_DT = now()
