mapper.select:
 	 	
		SELECT count(*) AS cnt
		FROM tb_aset_req
		WHERE req_no = #{params.reqNo};

mapper.update:
   		UPDATE tb_req_mas     
		   SET req_nm  = #{reqNm}
		     , req_rsn = #{reqRsn}
		 WHERE req_no = #{reqNo}

   		INSERT INTO tb_aset_req
   			   SET aset_no     = #{asetNo}
   			     , req_no	   = #{reqNo}
   			     , aset_nm	   = #{asetNm}
   			     , aset_type1  = #{asetType1}
   			     , aset_type2  = #{asetType2}
   			     , aset_type3  = #{asetType3}
   			     , mftco	   = #{mftco}
			     , model	   = #{model}
			     , sn		   = #{sn}
			     , chrgr	   = #{chrgr}
			     , biz_dept_cd = #{bizDeptCd}
			     , dept_cd	   = #{deptCd}
			   ON  DUPLICATE KEY UPDATE
			       aset_type1  = #{asetType1}
		         , aset_type2  = #{asetType2}
		         , aset_type3  = #{asetType3}
		         , mftco	   = #{mftco}
		         , model	   = #{model}
		         , sn		   = #{sn}
		         , chrgr	   = #{chrgr}

mapper.delete:
       	DELETE FROM tb_aset_req
		 WHERE aset_no = #{asetNo}
		  AND req_no = #{reqNo};
