mapper.select:
/* InspManageMapper.xml selectCountInspManageList */          
SELECT COUNT(*) AS CNT
   FROM TB_INSP_MAS TIM
  WHERE 1=1
  
   AND (INSP_ST_DT LIKE CONCAT(#{params.inspYear}, '%')) 
  
  
   AND INSP_NM = #{params.inspNm}
  
  
   AND INSP_NO = #{params.inspNo}

/* InspManageMapper.xml selectInspManageList */ SELECT INSP_NO /* 실사번호 */ , INSP_NM /* 실사명 */ , LEFT(INSP_ST_DT, 4) INSP_YEAR /* 실사연도*/ , DATE_FORMAT(INSP_ST_DT , "%Y-%m-%d") INSP_ST_DT /* 실사시작일 */ , DATE_FORMAT(INSP_END_DT, "%Y-%m-%d") INSP_END_DT /* 실사종료일 */ , CASE WHEN ONLINE_INSP_BASE_DT IS NULL THEN '' ELSE DATE_FORMAT(ONLINE_INSP_BASE_DT, "%Y-%m-%d") END ONLINE_INSP_BASE_DT /* 온라인실사기준일 */ , INSP_STUS /* 실사상태 */ , (SELECT TCC.CODE_NM FROM TB_COM_CODE TCC WHERE TCC.CODE_GRP_ID = 'INSP_MAS_STUS' AND TCC.CODE_ID = TIM.INSP_STUS AND TCC.USE_YN = 'Y' ) INSP_STUS_NM /* 실사상태명 */ , COALESCE((SELECT SUM(CASE WHEN CHAR_LENGTH(DTIM.INSPTR1) > 0 THEN 1 ELSE 0 END) + SUM(CASE WHEN CHAR_LENGTH(DTIM.INSPTR2) > 0 THEN 1 ELSE 0 END) + SUM(CASE WHEN CHAR_LENGTH(DTIM.INSPTR3) > 0 THEN 1 ELSE 0 END) + SUM(CASE WHEN CHAR_LENGTH(DTIM.INSPTR4) > 0 THEN 1 ELSE 0 END) + SUM(CASE WHEN CHAR_LENGTH(DTIM.INSPTR5) > 0 THEN 1 ELSE 0 END) FROM TB_INSP_MNG DTIM WHERE DTIM.INSP_NO = TIM.INSP_NO GROUP BY DTIM.INSP_NO) , 0) INSP_TARGET /* 실사대상자 */ , CONCAT((SELECT CONVERT(COUNT(*),CHAR) FROM TB_INSP_ASET TIA WHERE TIA.INSP_NO = TIM.INSP_NO AND TIA.INSP_CMPL_YN = 'Y'), '/', (SELECT CONVERT(COUNT(*),CHAR) FROM TB_INSP_ASET TIA WHERE TIA.INSP_NO = TIM.INSP_NO)) INSP_PROGRESS_RATE /* 부서별 진행상황 */ , UPDR , UP_DT , REGR , REG_DT FROM TB_INSP_MAS TIM WHERE 1=1
AND (INSP_ST_DT LIKE CONCAT(#{params.inspYear}, '%'))
AND INSP_NM = #{params.inspNm}
AND INSP_NO = #{params.inspNo}
ORDER BY INSP_NO DESC
LIMIT #{numOfRows} OFFSET #{start}

mapper.insert:
	/* InspManageMapper.xml insertInspMaster */
 INSERT INTO TB_INSP_MAS
 (INSP_NO, INSP_NM, INSP_ST_DT, INSP_END_DT, ONLINE_INSP_BASE_DT, INSP_STUS, UPDR, UP_DT, REGR, REG_DT)
 VALUES
 (
  CONCAT('R',DATE_FORMAT(NOW(),'%y%m'),LPAD( NEXTVAL(TAMS.SQ_INSP_NO) ,'3','0'))
 ,#{inspNm} 
 ,#{inspStDt}
 ,#{inspEndDt}
 ,#{onlineInspBaseDt}
 ,#{inspStus} /* NL정상 */
 ,#{regr}
 ,now()
 ,#{regr}
 ,now()
 )

	/* InspManageMapper.xml updateInspMaster */
 UPDATE TB_INSP_MAS
    SET INSP_NM             = #{inspNm}
      , INSP_ST_DT          = #{inspStDt}
      , INSP_END_DT         = #{inspEndDt}
      , ONLINE_INSP_BASE_DT = #{onlineInspBaseDt}
  WHERE INSP_NO = #{inspNo}

mapper.delete:
	/* InspManageMapper.xml deleteInspMaster */
 DELETE FROM TB_INSP_MAS
  WHERE INSP_NO = #{inspNo}

mapper.select:
/* InspManageMapper.xml selectCountInspAsetList */
SELECT COUNT(*)
  FROM TB_INSP_ASET TIM
 WHERE 1=1
  
   AND INSP_NO = #{params.inspNo}

/* InspManageMapper.xml selectInspAsetList */ SELECT TIA.INSP_NO /* 실사번호 */ , TIA.ASET_NO /* 자산번호*/ , TIA.ASET_NM /* 자산명*/ , TIA.ASET_TYPE1 /* 자산유형1 */ , (SELECT TCC.CODE_NM FROM TB_COM_CODE TCC WHERE TCC.CODE_GRP_ID = 'AS_CLASS' AND TCC.CODE_ID = TIA.ASET_TYPE1) ASET_TYPE1_NM , TIA.ASET_TYPE2 /* 자산유형2 */ , (SELECT TCC.CODE_NM FROM TB_COM_CODE TCC WHERE TCC.CODE_GRP_ID = 'AS_CLASS' AND TCC.CODE_ID = TIA.ASET_TYPE2) ASET_TYPE2_NM , TIA.ASET_TYPE3 /* 자산유형3 */ , (SELECT TCC.CODE_NM FROM TB_COM_CODE TCC WHERE TCC.CODE_GRP_ID = 'AS_CLASS' AND TCC.CODE_ID = TIA.ASET_TYPE3) ASET_TYPE3_NM , TIA.MFTCO /* 메이커 */ , TIA.MODEL /* 모델 */ , TIA.SN /* S/N */ , (SELECT TD.UPP_DEPT_CD FROM TB_DEPT TD WHERE TD.DEPT_CD = TIA.DEPT_CD) BIZ_DEPT_CD /* 사업부코드 */ , (SELECT DEPT_NM FROM TB_DEPT TBUP WHERE DEPT_CD = (SELECT UPP_DEPT_CD FROM TB_DEPT TD WHERE DEPT_CD = TIA.DEPT_CD)) BIZ_DEPT_NM /* 사업부명 */ , DEPT_CD /* 부서 */ , (SELECT DEPT_NM FROM TB_DEPT TD WHERE TD.DEPT_CD = TIA.DEPT_CD) DEPT_NM , TIA.CHRGR /* 담당자 */ , TIA.INSP_STUS /* 실사상태 */ , (SELECT CODE_NM FROM TB_COM_CODE TCC WHERE TCC.CODE_GRP_ID = 'INSP_STATUS' AND TCC.CODE_ID = TIA.INSP_STUS AND TCC.USE_YN = 'Y') INSP_STUS_NM , TIA.INSP_MTD /* 실사방법 */ , (SELECT CODE_NM FROM TB_COM_CODE TCC WHERE TCC.CODE_GRP_ID = 'INSP_METHOD' AND TCC.CODE_ID = TIA.INSP_MTD AND TCC.USE_YN = 'Y') INSP_MTD_NM , TIA.INSP_CMPL_YN /* 실사완료여부 */ , TIA.PTCL /* 특이사항 */ , TIA.RMK /* 비고 */ FROM TB_INSP_ASET TIA WHERE 1=1
AND INSP_NO = #{params.inspNo}
ORDER BY INSP_NO DESC
LIMIT #{numOfRows} OFFSET #{start}

/* InspManageMapper.xml selectInspAsetAddCoutList */
SELECT COUNT(*)
  FROM TB_ASET_MAS TAM
 WHERE 1=1
  
   AND ASET_NO = #{params.asetNo} 
  
  
   AND ASET_NM = #{params.asetNm}

/* InspManageMapper.xml selectInspAsetAddList */ SELECT TAM.ASET_NO /* 자산번호 */ , TAM.ASET_NM /* 자산명 */ , (SELECT TCC.CODE_NM FROM TB_COM_CODE TCC WHERE TCC.CODE_GRP_ID = 'AS_CLASS' AND TCC.USE_YN = 'Y' AND TCC.CODE_ID = TAM.ASET_TYPE1) ASET_TYPE1 /* 자산유형1 */ , (SELECT TCC.CODE_NM FROM TB_COM_CODE TCC WHERE TCC.CODE_GRP_ID = 'AS_CLASS' AND TCC.USE_YN = 'Y' AND TCC.CODE_ID = TAM.ASET_TYPE2) ASET_TYPE2 /* 자산유형2 */ , (SELECT TCC.CODE_NM FROM TB_COM_CODE TCC WHERE TCC.CODE_GRP_ID = 'AS_CLASS' AND TCC.USE_YN = 'Y' AND TCC.CODE_ID = TAM.ASET_TYPE3) ASET_TYPE3 /* 자산유형3 */ , TAM.MFTCO /* 제조사 */ , TAM.MODEL /* 모델 */ , TAM.SN /* S/N */ FROM TB_ASET_MAS TAM WHERE 1=1 AND NOT EXISTS (SELECT 1 FROM TB_INSP_ASET TIA WHERE TIA.INSP_NO = #{params.inspNo} AND TIA.ASET_NO = TAM.ASET_NO)
AND ASET_NO = #{params.asetNo}
AND ASET_NM = #{params.asetNm}
ORDER BY ASET_NO DESC
LIMIT #{numOfRows} OFFSET #{start}

mapper.update:
	/* InspManageMapper.xml insertInspAssetList */
    UPDATE TB_INSP_ASET SET
           INSP_STUS    = #{inspStus} 
         , INSP_MTD     = #{inspMtd}
         , INSP_CMPL_YN = #{inspCmplYn}
         , PTCL         = #{ptcl}
         , RMK          = #{rmk}
    WHERE INSP_NO  = #{inspNo}
      AND ASET_NO  = #{asetNo}

mapper.delete:
	/* InspManageMapper.xml deleteInspAsetList */
    DELETE FROM TB_INSP_ASET
     WHERE INSP_NO  = #{inspNo}
      
       AND ASET_NO  = #{asetNo}

	/* InspManageMapper.xml deleteInspMngList */
    DELETE FROM TB_INSP_MNG
     WHERE INSP_NO  = #{inspNo}
      
       AND DEPT_CD  = #{deptCd}

mapper.update:
	/* InspManageMapper.xml updateInspMngList */
    INSERT INTO TB_INSP_MNG (INSP_NO, DEPT_CD, DPLDR, INSPTR1, INSPTR2, INSPTR3, INSPTR4, INSPTR5, REGR, REG_DT)
    VALUES (#{inspNo}
          , #{deptCd}
          , #{dpldr}
          , #{insptr1}
          , #{insptr2}
          , #{insptr3}
          , #{insptr4}
          , #{insptr5}
          , #{userId}
          , NOW()
    )
        ON DUPLICATE KEY 
    UPDATE DPLDR   = #{dpldr}
         , INSPTR1 = #{insptr1}
         , INSPTR2 = #{insptr2}
         , INSPTR3 = #{insptr3}
         , INSPTR4 = #{insptr4}
         , INSPTR5 = #{insptr5}
         , UPDR    = #{userId}
         , UP_DT   = NOW()

