mapper.select:
SELECT B.CODE_ID as codeId
	 , B.CODE_NM as codeNm
FROM TB_COM_CODE B
WHERE B.USE_YN = 'Y'
  AND B.CODE_GRP_ID = #{codeGrpId}

  AND B.CODE_LVL = #{codeLvl}


  AND B.UPP_CODE_ID = #{upperCodeId}

ORDER BY sort_odr

mapper.update:
UPDATE TB_COM_FILE 
   SET USE_YN   = 'N'
     , UPDR     = #{updr}
 WHERE FILE_ID = #{fileId}

UPDATE TB_COM_FILE 
   SET DWLD_CNT = DWLD_CNT + 1
     , UPDR     = #{updr}
 WHERE FILE_ID = #{fileId}
