mapper.update:
UPDATE tb_com_code         
   SET code_nm     = #{codeNm}
     , code_eng_nm = #{codeEngNm}
     , upp_code_id = #{uppCodeId}
     , code_lvl    = #{codeLvl}
     , code_desc   = #{codeDesc}
	 , sort_odr    = #{sortOdr}
	 , resv1       = #{resv1}
	 , resv2       = #{resv2}
	 , resv3       = #{resv3}
	 , use_yn      = #{useYn}
	 , updr        = #{updr}
	 , up_dt       = current_timestamp()
WHERE code_id = #{codeId} AND code_grp_id = #{codeGrpId}

mapper.delete:
		DELETE 
		FROM tb_com_code 
        WHERE code_id = #{codeId} AND code_grp_id = #{codeGrpId}
