mapper.select:
SELECT a.inv_no as invNo , a.inv_ttl as invTtl , IFNULL(a.inv_qty, 0) as invQty , IFNULL(a.inv_amt, 0) as invAmt , a.actg_year as actgYear , a.po_no as poNo , a.mfgd_nm as mfgdNm , a.qty as qty , a.po_amt as poAmt , date_format(a.inv_dt, '%Y-%m-%d') as invDt , a.dept_nm as deptNm , a.inv_reqr as invReqr , a.user_nm as userNm , (SELECT CASE WHEN COUNT(*) >= 1 THEN 'Y' ELSE 'N' END FROM TB_PO_ASET WHERE po_no = a.po_no ) as poAsetYn , a.reg_dt as regDt FROM ( SELECT ROW_NUMBER() OVER(ORDER BY CAST(a.inv_no AS int)) as rn , a.inv_no , a.inv_ttl , a.inv_qty , a.inv_amt , a.actg_year , b.po_no , b.mfgd_nm , b.qty , b.po_amt , a.inv_dt , e.dept_nm , a.inv_reqr , d.user_nm , a.reg_dt FROM TB_INV_MAS A, TB_PO B, TB_USER D, TB_DEPT E WHERE 1 = 1 AND A.inv_no = B.inv_no AND D.use_yn = 'Y' AND A.inv_reqr = D.user_id AND D.dept_cd = E.dept_cd ) AS a WHERE rn BETWEEN #{start} AND #{end}
AND ${params.searchType} LIKE CONCAT('%', #{params.searchText}, '%')
AND ${params.searchType2} LIKE CONCAT('%', #{params.searchText2}, '%')
ORDER BY ${params.sortField} ${params.sortOrder}
ORDER BY poNo asc
