mapper.select:
SELECT user_id , bbs_grp_id , bbs_dp , bbs_ttl , bbs_cn , view_cnt , del_yn , updr , DATE_FORMAT(up_dt, '%Y-%m-%d') AS up_dt , regr , DATE_FORMAT(reg_dt, '%Y-%m-%d') AS reg_dt , bbs_id FROM ( SELECT user_id , bbs_grp_id , bbs_dp , bbs_ttl , bbs_cn , view_cnt , del_yn , updr , up_dt , regr , reg_dt , bbs_id , ROW_NUMBER() OVER(ORDER BY bbs_id) AS rn FROM TB_COM_BBS WHERE 1=1
AND ( bbs_grp_id LIKE CONCAT('%', #{params.searchText}, '%') OR bbs_ttl LIKE CONCAT('%', #{params.searchText}, '%'))
AND bbs_grp_id LIKE CONCAT('%', #{params.searchText}, '%')
AND bbs_ttl LIKE CONCAT('%', #{params.searchText}, '%')
AND del_yn = #{params.delYnOption}
) AS a WHERE rn BETWEEN #{start} AND #{end} ORDER BY bbs_id

mapper.insert:
INSERT INTO TB_COM_BBS ( 
       user_id
     , bbs_grp_id
     , bbs_dp
     , bbs_ttl
     , bbs_cn
     , view_cnt
     , regr
	 , reg_dt
) VALUES (
       #{userId}
     , #{bbsGrpId}
     , #{bbsDp}
     , #{bbsTtl}
     , #{bbsCn}
     , #{viewCnt}
     , #{regr}
     , current_timestamp()
)

mapper.update:
UPDATE TB_COM_BBS         
   SET bbs_grp_id = #{bbsGrpId}
     , bbs_ttl    = #{bbsTtl}
     , bbs_cn     = #{bbsCn}
     , updr       = #{updr}
	 , up_dt      = current_timestamp()
 WHERE bbs_id = #{bbsId}

mapper.select:
SELECT user_id , bbs_grp_id , bbs_dp , bbs_ttl , bbs_cn , view_cnt , del_yn , updr , DATE_FORMAT(up_dt, '%Y-%m-%d') AS up_dt , regr , DATE_FORMAT(reg_dt, '%Y-%m-%d') AS reg_dt , bbs_id FROM ( SELECT user_id , bbs_grp_id , bbs_dp , bbs_ttl , bbs_cn , view_cnt , del_yn , updr , up_dt , regr , reg_dt , bbs_id , ROW_NUMBER() OVER(ORDER BY bbs_id) AS rn FROM TB_COM_BBS WHERE del_yn = 'N'
AND ( bbs_ttl LIKE CONCAT('%', #{params.searchText}, '%'))
AND bbs_ttl LIKE CONCAT('%', #{params.searchText}, '%')
AND del_yn = #{params.delYnOption}
) AS a WHERE rn BETWEEN #{start} AND #{end} ORDER BY bbs_id

mapper.update:
   		UPDATE TB_COM_BBS         
		   SET view_cnt = view_cnt + 1
		 WHERE bbs_id = #{bbsId}
