mapper.select:
WITH RECURSIVE CTE AS ( SELECT MENU_ID, MENU_NM, UPP_MENU_ID, MENU_LVL , URL, MENU_DESC , USE_YN, POPUP_YN, SORT_ODR, REGR, REG_DT, UPDR, UP_DT FROM TB_COM_MENU WHERE MENU_LVL = 1 UNION ALL SELECT B.MENU_ID, B.MENU_NM, B.UPP_MENU_ID, B.MENU_LVL , B.URL, B.MENU_DESC , B.USE_YN, B.POPUP_YN, B.SORT_ODR, B.REGR, B.REG_DT, B.UPDR, B.UP_DT FROM TB_COM_MENU B INNER JOIN CTE C ON B.UPP_MENU_ID = C.MENU_ID ) , MENUS AS ( SELECT MENU_ID AS menuId , MENU_NM AS menuNm , UPP_MENU_ID AS uppMenuId , MENU_LVL AS lvl , URL AS url , CASE WHEN MENU_LVL = 1 THEN MENU_DESC WHEN MENU_LVL = 2 THEN CONCAT('â”” ',SUBSTR(MENU_DESC, INSTR(MENU_DESC,'>') + 2)) ELSE '' END as menuDesc , USE_YN AS useYn_2 , POPUP_YN AS popupYn , SORT_ODR AS sortOdr , REGR AS regr , REG_DT AS regDt , UPDR AS updr , UP_DT AS upDt FROM CTE ORDER BY MENU_ID ) SELECT '' as chk , menuId , menuDesc as menuNm , CASE WHEN menuYn = '' THEN false ELSE true END as menuYn , CASE WHEN inqrYn = '' THEN false ELSE true END as inqrYn , CASE WHEN regYn = '' THEN false ELSE true END as regYn , CASE WHEN updYn = '' THEN false ELSE true END as updYn , CASE WHEN delYn = '' THEN false ELSE true END as delYn , IFNULL(regr, '') as regr , IFNULL(regDt, '') as regDt , IFNULL(updr, '') as updr , IFNULL(upDt, '') as upDt , #{params.roleId} as groupId , rn FROM ( SELECT ROW_NUMBER() OVER(ORDER BY CAST(m.menuId AS int)) AS rn , m.* , IFNULL(b.menu_id, '') as menuId2 , IFNULL(b.menu_yn, '') as menuYn , IFNULL(b.inqr_yn, '') as inqrYn , IFNULL(b.reg_yn, '') as regYn , IFNULL(b.upd_yn, '') as updYn , IFNULL(b.del_yn, '') as delYn FROM MENUS m LEFT OUTER JOIN( SELECT * FROM TB_COM_MENU_ROLE b ) b ON 1=1 AND menuId = b.menu_id
AND b.role_id = #{params.roleId}
) A

mapper.update:
		UPDATE TB_COM_MENU 
		   SET MENU_NM	 	= #{menuNm}
		     , URL	 		= #{url}

             , UPP_MENU_ID	= #{uppMenuId}

		     , MENU_DESC	= #{menuDesc}
		     , MENU_LVL		= #{lvl}
		     , POPUP_YN		= #{popupYn}
		     , SORT_ODR		= #{sortOdr}
		     , USE_YN		= #{useYn}
		     , UPDR			= #{updr}
		     , UP_DT		= current_timestamp()
        WHERE  MENU_ID = #{menuId}
